import React, { useState } from "react"

// console.log(useState('Enter text here2'));

export default function TextForm(props) {
    const handleUpClick = () => {
        // console.log("Uppercase was clicked" + text)
        let newText = text.toUpperCase()
        setText(newText)
        props.showAlert("Text is converted to Uppercase!", "success")
    }
    const handleLowClick = () => {
        // console.log("Lowercase was clicked" + text)
        let newText = text.toLowerCase()
        setText(newText)
        props.showAlert("Text is converted to Lowercase!", "success")
    }
    const handleCopy = () => {
        var text = document.getElementById("MyBox")
        text.select() //we dont need to select and deselect anymore
        navigator.clipboard.writeText(text.value)
        document.getSelection().removeAllRanges()
        props.showAlert("Text copied to clipboard!", "success")
    }
    const handleExtraSpaces = () => {
        let newText = text.split(/[ ]+/)
        setText(newText.join(" "))
        props.showAlert("Extra spaces removed!", "success")
    }
    const handleClearText = () => {
        // console.log("Clear was clicked" + text)
        let newText = ""
        setText(newText)
        props.showAlert(
            "Text box is cleared. Enter new text to manipulate!",
            "success"
        )
    }
    const handleOnChange = (event) => {
        // console.log("On change")
        setText(event.target.value)
    }
    var num = 0

    const [text, setText] = useState("") //setText is the update function
    // text="new text"; //wrong way
    // setText("New text"); //correct way
    return (
        <>
            <div className="container">
                <h1>{props.heading}</h1>
                <div className="mb-3">
                    {/* <label for="MyBox" className="form-label">Example textarea</label> */}
                    <textarea
                        className="form-control"
                        value={text}
                        onChange={handleOnChange}
                        style={{
                            backgroundColor:
                                props.mode === "light" ? "dark" : "light",
                        }}
                        id="MyBox"
                        rows="8"
                    ></textarea>
                </div>
                <button
                    disabled={text.length === 0}
                    className="btn btn-primary mx-1 my-1"
                    onClick={handleUpClick}
                >
                    Convert to Uppercase
                </button>
                <button
                    disabled={text.length === 0}
                    className="btn btn-primary mx-1 my-1" //mx-1 creates space between 2 components; here, buttons
                    onClick={handleLowClick}
                >
                    Convert to Lowercase
                </button>
                <button
                    disabled={text.length === 0}
                    className="btn btn-primary mx-1 my-1" //mx-1 creates space between 2 components; here, buttons
                    onClick={handleCopy}
                >
                    Copy Text
                </button>
                <button
                    disabled={text.length === 0}
                    className="btn btn-primary mx-1 my-1" //mx-1 creates space between 2 components; here, buttons
                    onClick={handleExtraSpaces}
                >
                    Handle Extra Spaces
                </button>
                <button
                    disabled={text.length === 0}
                    className="btn btn-primary mx-1 my-1" //mx-1 creates space between 2 components; here, buttons
                    onClick={handleClearText}
                >
                    Clear text
                </button>
            </div>
            <div className="container my-3">
                <h2>Your text summary</h2>
                <p>
                    {
                        text.split(/\s+/).filter((element) => {
                            return element.length !== 0
                        }).length
                    }{" "}
                    words and {text.length} characters
                    {/*number count*/}
                </p>
                <p>
                    {0.008 *
                        text.split(" ").filter((element) => {
                            return element.length !== 0
                        }).length}{" "}
                    minutes reading time.
                    {/*1 word = 0.008 minutes(1/125) multiplied by length*/}
                </p>
                <p>
                    {" "}
                    Total numbers of sentences are{" "}
                    {
                        text.split(".").filter((element) => {
                            return element.length !== 0
                        }).length
                    }
                </p>

                <h3>Preview</h3>
                <p>
                    {text.length > 0
                        ? text
                        : "Enter your text in the text box to preview it here"}
                </p>
            </div>
        </>
    )
}
